mapper.delete:
delete
from course
where jwc_account = #{jwcAccount} and xnxqh = #{semester}

mapper.select:
select *
from course
where jwc_account = #{jwcAccount}
  and xnxqh = #{semester}

select distinct(location)
from course
where section = #{section}
  and weekday = #{weekDay}
  and xnxqh = #{semester}
  and locate("-" + #{week} + "-", week_seq) > 0;

select location
from free_classroom
where section = #{section}
  and weekday = #{weekDay}
  and xnxqh = #{semester}
  and week = #{week}
  and building = #{building}
